sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && g_form.isNewRecord()) {
			g_form.setValue('u_request_type',"Selectrequest type");

		}

	//if (isLoading || newValue === '') {
		if (newValue === '') {
			//g_form.setValue('u_what_needed','Whatneeded');
			return;
		}
		//Set requested for value this should be written on On form Load

		//var Requestedfor = g_user.getFullName();
		//alert(+ Requestedfor);
		//g_form.setValue('u_requested_for',Requestedfor);

		//Clear the Choices in what needed on request type change
		g_form.setValue('u_what_needed',"Whatneeded");
		g_form.clearOptions('u_what_needed');

		//Add Choices in what needed based on the Request type Value

		if (newValue == 'hr') {
			g_form.addOption('u_what_needed', 'Facilites1','Facilites1');
			g_form.addOption('u_what_needed', 'Facilites2','Facilites2');
		}
		if (newValue == 'facilities') {
			g_form.addOption('u_what_needed', 'HR1','HR1');
			g_form.addOption('u_what_needed', 'HR2','HR2');
		}
		if (newValue == 'legal') {
			g_form.addOption('u_what_needed', 'llc1','LLC1');
			g_form.addOption('u_what_needed', 'llc2','LLC2');
		}

		//Type appropriate comment here, and begin script below

	}
